mapper.insert:
INSERT INTO material_temp ( material_no, material_name, foreign_name, material_type, brand, specifications_model, season, is_purchase, is_sell, is_inventory, unit_id, barcode ) SELECT * FROM ( SELECT t2.material_no, t2.material_name, t2.foreign_name, t3.id typeId, t2.brand, t2.specifications_model, t2.season, CASE t2.is_purchase WHEN '是' THEN 1 ELSE 0 END is_purchase, CASE t2.is_sell WHEN '是' THEN 1 ELSE 0 END is_sell, CASE t2.is_inventory WHEN '是' THEN 1 ELSE 0 END is_inventory, t4.id unitId, t2.barcode FROM ${tableName} t2 LEFT OUTER JOIN material_type t3 ON t3.type_name = t2.material_type LEFT OUTER JOIN material_unit t4 ON t4.unit_name = t2.unit_id
limit #{startNo},#{pageSize}
) AS tb
