mapper.select:
select m.id pkey, m.material_no materialNo, m.material_name materialName, t.type_name typeName, m.brand brand, m.specifications_model specification, m.barcode barcode, m.expiration_date expirationDate, m.create_date addTime, u.unit_name unitName from material m left join material_type t on m.material_type = t.id left join material_unit u on m.unit_id = u.id
where m.material_name like CONCAT ('%', #{keyword},'%')
limit #{startNo},#{pageSize}
